allow:
Bash(/tmp/verify_brian.ps1 << 'PSEOF'
$basePath = "C:\\Alpha\\UB-CIM \(non fiction Author\)\\.q-system"

Write-Output "=== Checking for 'Brian' in file content ==="
$contentMatches = Get-ChildItem -Path $basePath -Recurse -Filter "*.md" | Select-String -Pattern 'Brian'
if \($contentMatches\) {
    $contentMatches | ForEach-Object { Write-Output "$\($_.Path\):$\($_.LineNumber\): $\($_.Line\)" }
} else {
    Write-Output "No occurrences of 'Brian' found in any .md file content."
}

Write-Output ""
Write-Output "=== Checking for 'Brian' in filenames ==="
$nameMatches = Get-ChildItem -Path $basePath -Recurse | Where-Object { $_.Name -like '*Brian*' }
if \($nameMatches\) {
    $nameMatches | ForEach-Object { Write-Output $_.FullName }
} else {
    Write-Output "No files with 'Brian' in their filename."
}
PSEOF)
